var path = require('path');
var pkg = require('../../package');
require('babel/register');

exports.requireTest = function(filename) {
  var basepath = path.relative(__filename, filename);
  return require(basepath);
};

exports.requireMocks = function(filename){
  var basepath = path.relative(filename, "./test/mocks/");
  return require(basepath);
};

exports.basePath= function(filename){
  var basepath = path.relative(filename, "./test");
  return basepath;
};
